'use strict';

module.exports = function($stateProvider, $urlRouterProvider, $locationProvider) {
	$locationProvider.html5Mode(true);

	$urlRouterProvider.otherwise('/');

	$stateProvider
		.state('home', {
			url: '/',
			templateUrl: 'home.html'
		})
		.state('about', {
			url: '/about',
			templateUrl: 'about.html'
		})
		.state('todo', {
			url: '/todo*status',
			controller: 'TodoCtrl',
			templateUrl: 'todo.html'
		});
};